import React, { useEffect, useState } from "react";
import { BrowserRouter, Route, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import "./App.css";
import AddContact from "./components/AddContact";
import ContactCard from "./components/ContactCard";
import { getContacts, addContact } from "./js/actions/actionsContact";
// import store from "./js/store/index";

function App() {
  const [contact, setContact] = useState({
    name: "",
    email: "",
    phone: "",
  });

  const dispatch = useDispatch();
  const contacts = useSelector((state) => state.contacts);

  const handelChange = (e) => {
    setContact({ ...contact, [e.target.name]: e.target.value });
  };

  const add = () => {
    dispatch(addContact(contact));
  };

  useEffect(() => {
    dispatch(getContacts());
  }, []);
  return (
    <BrowserRouter>
      <div>
        <Link to="/Contact-list">
          <button className="button">Contact List</button>
        </Link>
        <Link to="/Add-contact">
          <button className="button">Add Contact</button>
        </Link>
      </div>
      <Route
        path="/Contact-list"
        render={() => (
          <div className="contact-list">
            {contacts.map((el, i) => (
              <ContactCard key={i} contact={el} />
            ))}
          </div>
        )}
      />
      <Route
        path="/Add-contact"
        render={() => <AddContact handelChange={handelChange} action={add} />}
      />
    </BrowserRouter>
  );
}

export default App;
